package level4;

public class Pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++){
			//no of $'s
			for(int j=5;j>=i;j--)
			{
			System.out.print(" ");
			}
			//no of *'s
			System.out.print("*****");
			
			System.out.println();
			
	}

}
}